query AllAppointments {
    appointmentById(id: 2) {
        id
        appointmentDate
        status
        notes
        createdAt
        updatedAt
        patient {
            id
            name
            dateOfBirth
            document
            phone
            email
            address
        }
        nurse {
            id
            name
            coren
        }
        doctor {
            id
            name
            specialty
            crm
        }
        medicalRecord {
                id
                diagnosis
                prescription
                observations
                createdAt
                updatedAt
            }
    }
}

