query Appointments {
    appointments(page: { page: 0, size: 50 } 
        filter: { futureOnly : true} ) {
        content {
            id
            appointmentDate
            status
            notes
            createdAt
            updatedAt
            patient {
                id
                name
                dateOfBirth
                document
                phone
                email
                address
            }
            doctor {
                id
                name
                specialty
                crm
            }
            nurse {
                id
                name
                coren
            }
        }
        pageInfo {
            page
            size
            totalPages
            totalElements
        }
    }
}

